import "./App.css";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "react-datepicker/dist/react-datepicker.css";
import React, { useEffect, useState } from "react";
import DatePicker from "react-datepicker";
import UserService from "./api/UserService";
import axios from "axios";

const locales = {
  "en-US": require("date-fns/locale/en-US"),
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

function Home() {
  const [newEvent, setNewEvent] = useState({});
  const [period, setPeriod] = useState("");
  const [events, setEvents] = useState([]);

  // useEffect(() => {
  //   axios
  //     .get("http://localhost:8080/event/get/2")
  //     .then((res) => {
  //       console.log(res);
  //       setEvents(res.data);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // });

  function handleShowPeriod() {}

  function enterEvent() {
    useEffect(() => {
      axios.get("http://localhost:8080/currentUser/get/"+ localStorage.getItem("JWTToken"))
      .then((res) => {
        console.log(res)
      })
    })



    // try {
    // const us = UserService.getUser();
    // console.log(us);
    // const userID = (await us).data.id;
    // UserService.enterEvent(2);
    // }catch(err) {

    // }
  }

  return (
    <div>
      {/* <ul style={{ marginRight: "50%" }}>
        {events.map((event) => (
          <li key={event.id}>
            {event.title}-{event.text}-{event.date}
          </li>
        ))}
      </ul> */}
      <div className="Calendar">
        <h1> Calendar</h1>
        <style>{"body {color:black}"}</style>
        <Calendar
          localizer={localizer}
          events={period}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500, margin: "50px" }}
        />
        <div style={{ marginLeft: "70%" }}>
          <input
            type="text"
            id="eventId"
            placeholder="Add TODO"
            style={{ width: "100%", margin: "10px" }}
          />
          <input
            type="text"
            id="TODOText"
            placeholder="What do u want"
            autoComplete="off"
            aria-describedby="uidnote"
          />

          <DatePicker
            id="endDateId"
            dateFormat="yyyy-MM-dd"
            placeholderText="When"
            style={{ marginLeft: "70%" }}
            selected={newEvent.end}
            onChange={(end) => setNewEvent({ ...newEvent, end })}
          ></DatePicker>

          <button
            style={{ marginTop: "5%", marginLeft: "-50%" }}
            onClick={handleShowPeriod}
          >
            Show Period Info
          </button>
          <button style={{ marginTop: "5%" }} onClick={enterEvent}>
            Add new TODO
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
