import "./App.css";
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format";
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek";
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "react-datepicker/dist/react-datepicker.css";
import React, { useEffect, useState } from "react";
import DatePicker from "react-datepicker";
import UserService from "./api/UserService";
import axios from "axios";
import { DatePickerComponent } from "@syncfusion/ej2-react-calendars";
import "react-datepicker/dist/react-datepicker.css"

const locales = {
  "en-US": require("date-fns/locale/en-US"),
};

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales,
});

const per = [{}];

function Home() {
  const [newEvent, setNewEvent] = useState({});
  const [events, setEvents] = useState([]);
  const [period, setPeriod] = useState(per);

  const showPeriod = async (e) => {
    e.preventDefault();
    try {
      const tu = UserService.getPeriod();
      const newPeriod = {
        title: "Period",
        start: (await tu).data.startDate,
        allDay: true,
        end: (await tu).data.endDate,
      };
      setPeriod([...period, newPeriod]);
      // console.log(tu.data)
    } catch (err) {}
  };

  useEffect(() => {
    axios
      .get(
        "http://localhost:8080/event/get/" + localStorage.getItem("JWTToken")
      )
      .then((res) => {
        // console.log(res);
        setEvents(res.data);
      })
      .catch((err) => {
        //console.log(err);
      });
  }, []);

  const enterEvent = async (e) => {
    e.preventDefault();
    try {
      const us = UserService.getUser();
      const userId = (await us).data.id;
      console.log(userId);
      UserService.enterEvent(userId);
    } catch (err) {}
  };

  const enterPeriod = async(e) => {
    try {
    e.preventDefault();
    const us = UserService.getUser();
    const userId = (await us).data.id;
    UserService.enterPeriod(userId);
  } catch(err) {}
};

  return (
    <div>
      {
        <ul style={{ marginRight: "50%" }}>
          {events.map((event) => (
            <li key={event.id}>
              {event.title}-{event.text}-{event.date}
            </li>
          ))}
        </ul>
      }
      <div className="Calendar">
        <h1> Calendar</h1>
        <style>{"body {color:black}"}</style>
        <Calendar
          localizer={localizer}
          events={period}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500, margin: "50px" }}
        />
        <div style={{ marginLeft: "70%" }}>
          <input
            type="text"
            id="eventId"
            placeholder="Add TODO"
            style={{ width: "100%", margin: "10px" }}
          />
          <input
            type="text"
            id="TODOText"
            placeholder="What do u want"
            autoComplete="off"
            aria-describedby="uidnote"
          />

          <DatePicker
            id="endDateId"
            dateFormat="yyyy-MM-dd"
            placeholderText="When"
            style={{ marginLeft: "0%" }}
            selected={newEvent.end}
            onChange={(end) => setNewEvent({ ...newEvent, end })}
          ></DatePicker>

          <button
            style={{ marginTop: "5%", marginLeft: "-50%" }}
            onClick={showPeriod}
          >
            Show Period Info
          </button>
          <button style={{ marginTop: "5%" }} onClick={enterEvent}>
            Add new TODO
          </button>
        </div>
        <div style={{ marginRight: "70%" }}>
          <DatePickerComponent
            id="PeriodStartDate"
            datetFormat = 'yyyy/mm/dd'
            placeholder="When did her period start"
           
          ></DatePickerComponent>
          <button style={{ marginTop: "5%" }} onClick={enterEvent}>
            Enter new period schedule
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
