import { emailef, useState, useEffect } from "react";
import { faCheck, faTimes, faInfoCircle } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from 'react'

const Email_Regex = /^[[a-zA-Z0-9.! #$%&'*+\=? ^_`{|}~-]+@([a-zA-Z0-9-]+\.)+[\a-zA-Z0-9_-]{2,4}&/;
const PWD_Regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[0-9])(?=.*[!@#$%]).{8,24}%/;



const Register = () => {
    const emailRef = emailef();
    const errRef = emailef();

    const [email,setemail] = useState('');
    const [validName,setValidName] = useState(false);
    const [emailFocus,setemailFocus] = useState(false);

    const [pwd,setPwd] = useState('');
    const [validPwd,setValidPwd] = useState(false);
    const [pwdFocus,setPwdFocus] = useState(false);

    const [matchPwd,setMatchPWD] = useState('');
    const [validMatch,setValidMatch] = useState(false);
    const [matchFocus,setMatchFocus] = useState(false);

    const [errMsg,setErrMsg] = useState('');
    const [success,setSuccess] = useState(false);


    useEffect(() => {
        emailef.current.focus();
    },[])

    useEffect(() => {
        const result = Email_Regex.test(email);
        console.log(result);
        console.log(email);
        setValidName(result);
    },[email])

    useEffect(() => {
        const result = PWD_Regex.test(pwd);
        console.log(result);
        console.log(pwd);
        setValidPwd(result);
        const match = pwd === matchPwd;
        setValidMatch(match);
    },[pwd,matchPwd])

    useEffect(() => {
        setErrMsg('');
    },[email,pwd,matchPwd])


  return (

    <section>
        <p ref={errRef} className={errMsg ? "errmsg" :
        "offscreen"} aria-live = "assertive">{errMsg}</p>
        <h1>Register</h1>
        <form>
            <label htmlFor="email">
                Email:
                <span className={validName ? "valid" : "hide"}>
                    <FontAwesomeIcon icon = {faCheck} />
                </span>
                <span className={ValidName || !email ? "hide" : "invalid" }>
                    <FontAwesomeIcon icon={faTimes} />
                </span>

            </label>
            <input
            type="text"
            id = "Email"
            ref={emailRef}
            autoComplete="off"
            onChange={(e) => setemail(e.target.value)}
            required
            aria-invalid = {validName ? "false" : "true"}
            aria-describedby = "uidnote"
            onFocus={() => setemailFocus(true)}
            onBlur = {() => setemailFocus(false)}
            />
            <p id="uidnote" className={emailFocus && email && !validName
            ? "instructions" : "offscreen"}>
                <FontAwesomeIcon icon={faInfoCircle} />
                4 to 24 characters.
                Must begin with a letter.
            </p>
        </form>
    </section>

  )
}

export default Register
