import axios from 'axios';
//const POSTS_REST_API_URL = 'http://localhost:8080/api/posts';
const POSTS_REST_API_URL_POST = 'http://localhost:8080/registration';
class PostService {
    getPosts(){
       return axios.get(POSTS_REST_API_URL);
    }
    getPostsByKeyword(keyword){
       return axios.get(POSTS_REST_API_URL+"/"+keyword);
    }
    savePost(){
        let firstNameVal = document.getElementById("FirstName").value;
        let LastNameVal = document.getElementById("LastName").value;
        let emailVal = document.getElementById("email").value;
        let passwordVal = document.getElementById("password").value;
        let AddressVal = document.getElementById("Address").value;
        let dobVal = document.getElementById("DOB").value;
        let postToSave = {firstName:firstNameVal,
            lastName:LastNameVal,
            email:emailVal,
            password:passwordVal,
            dob:dobVal,
            address:AddressVal};
        return axios.post(POSTS_REST_API_URL_POST,postToSave);
     }
}
export default new PostService();
