import "./App.css"
import {Calendar,dateFnsLocalizer} from "react-big-calendar"
import format from 'date-fns/format';
import parse from 'date-fns/parse';
import startOfWeek from 'date-fns/startOfWeek';
import getDay from 'date-fns/getDay';
import "react-big-calendar/lib/css/react-big-calendar.css"
import "react-datepicker/dist/react-datepicker.css"
import React,{useState,useEffect, Component} from 'react';
import DatePicker from "react-datepicker"
import UserService from "./api/UserService";
import axios from "axios";
import { render } from "@testing-library/react";

const locales = {
  "en-US":require("date-fns/locale/en-US")
}

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales
})




// const events = [{
//   title:"Big Meeting",
//   allDay:true,
//   start:new Date(2022,6,14),
//   end:new Date(2022,6,14)
// },
// {
//   title:"Period Starts",
//   allDay:true,
//   start:new Date(2022,6,2),
//   end:new Date(2022,6,10)
// },
// {
//   title:"Conference",
//   allDay:true,
//   start:new Date(2022,5,14),
//   end:new Date(2022,5,14)
// }]
function createEvent(title,startDate,endDate) {
  return {
    title: title,
    startDate: startDate,
    endDate: endDate,
  };
}


function Home() {
 
  const [newEvent,setNewEvent] = useState({title:"",start:"",end:""})
// const [allEvents,setAllEvents] = useState([])
  const allEvents = {}
 
  const handleSubmit = async (e) => {
   await axios.get('http://localhost:8080/event/get/1').
    then(function(response) {
    let appointments = response.data;
    for(let i = 0;i <appointments.length;i++) {
      let Event = {
        title:appointments[i].title,
        start:appointments[i].startDate,
        end:appointments[i].endDate
      }
      //console.log(Event)
      console.log(Event);
     // setAllEvents([...allEvents,Event])
    }
    //console.log(setAllEvents)
    //setAllEvents(response.data)
    //console.log(setAllEvents);
    })

 }

    function handleAddEvent() {
      UserService.enterEvent();
      //setAllEvents([...allEvents,newEvent])
    handleSubmit();
    }
    
   
    return (
      <div className='Calendar'>
        <h1> Calendar</h1>
        
         <style>{'body {color:black}'}</style>
         <Calendar localizer={localizer}
         events = {allEvents} 
         startAccessor="start" 
         endAccessor="end"
        style={{height: 500,margin:"50px"}} />    
        <div
        style={{marginLeft:"70%"}}
        >
          <input 
          type="text"
          id="eventId"
          placeholder="Add event name"
          style={{width:"100%",margin:"10px"}}
          value = {newEvent.title}
          onChange = {(e) => setNewEvent({...newEvent,title:e.target.value})}
           />
           <DatePicker
           id="startDateId"
           dateFormat='yyyy-MM-dd'
           placeholderText="Start Date"
           style={{marginLeft:"70%"}}
           selected = {newEvent.start}
           onChange ={(start) => setNewEvent({...newEvent,start})}
           >
           </DatePicker>
          
          <DatePicker
          id="endDateId"
          dateFormat='yyyy-MM-dd'
          placeholderText="End Date"
          style={{marginLeft:"70%"}}
          selected = {newEvent.end}
          onChange = {(end) => setNewEvent({...newEvent,end})}
          >

          </DatePicker>


           <button style={{marginTop:"5%"}}
           onClick = {handleAddEvent}
           >
            
            Add event
           </button>
        </div>  
      </div>

      
    );
  }

export default Home;
